"use strict"
let assert, XmlDSigJs, XmlCore;

if (typeof module !== "undefined") {
    var config = require("../config");
    XmlDSigJs = config.xmldsig;
    XmlCore = config.XmlCore;
    assert = config.assert;
}

const x509_rsa = XmlCore.XmlBase64Converter.set(``);
const x509_ecdsa = XmlCore.XmlBase64Converter.set(``);

describe("PKI X509", () => {

    it("read error", () => {
        assert.throws(() => {
            new XmlDSigJs.X509Certificate(new Uint8Array([1, 2, 3, 4, 5, 6, 7]))
        });
    });

    it("read", () => {
        const x509 = new XmlDSigJs.X509Certificate(x509_rsa);

        assert.equal(!!x509.simpl, true);
        assert.equal(x509.SerialNumber, "11816535815648863340");
        assert.equal(x509.Issuer, "C=AT, O=Rundfunk und Telekom Regulierungs-GmbH, CN=Trusted List CA 1");
        assert.equal(x509.Subject, "C=AT, O=Rundfunk und Telekom Regulierungs-GmbH, CN=Trusted List 4");
        assert.equal(x509.PublicKey, null);
        assert.equal(x509.GetRaw().byteLength, 1087);

    });

    it("thumbprint default sha1", done => {
        const x509 = new XmlDSigJs.X509Certificate(x509_rsa);

        x509.Thumbprint()
            .then(hash => {
                assert.equal(XmlCore.XmlBase64Converter.get(new Uint8Array(hash)), "8fkJGt2sWZVfYUCi914gPj5wS3E=");
                return;
            })
            .then(done, done);
    });

    it("thumbprint sha256", done => {
        const x509 = new XmlDSigJs.X509Certificate(x509_rsa);

        x509.Thumbprint("SHA-256")
            .then(hash => {
                assert.equal(XmlCore.XmlBase64Converter.get(new Uint8Array(hash)), "3SYVGIWfXYYHrbeFU3LSrvEbN9HBVXRqJwQjSLhqStU=");
                return;
            })
            .then(done, done);
    });

    it("exportKey rsa", done => {
        Promise.resolve()
            .then(() => {
                const x509 = new XmlDSigJs.X509Certificate(x509_rsa);

                assert.equal(x509.PublicKey, null);

                return x509.exportKey({ name: "RSASSA-PKCS1-v1_5", hash: { name: "SHA-256" } })
                    .then(key => {
                        assert.equal(!!key, true);
                        assert.equal(key.type, "public");
                        assert.equal(key.algorithm.name, "RSASSA-PKCS1-v1_5");
                        assert.equal(key.algorithm.hash.name, "SHA-256");

                        assert.equal(x509.PublicKey, key);

                        return;
                    });
            })
            .then(done, done);
    });

    it("exportKey ecdsa", done => {
        Promise.resolve()
            .then(() => {
                const x509 = new XmlDSigJs.X509Certificate(x509_ecdsa);

                assert.equal(x509.PublicKey, null);

                return x509.exportKey({ name: "ECDSA", namedCurve: "P-256" })
                    .then(key => {
                        assert.equal(!!key, true);
                        assert.equal(key.type, "public");
                        assert.equal(key.algorithm.name, "ECDSA");
                        assert.equal(key.algorithm.namedCurve, "P-256");

                        assert.equal(x509.PublicKey, key);

                        return;
                    });
            })
            .then(done, done);
    });

});